package LeetCode;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.HashSet;

public class findRepeatedDnaSequences {
	
	public static ArrayList<String> findRepeatedDnaSequences(String s) {
		HashSet<Integer> set=new HashSet<Integer>();
		HashSet<Integer> rset=new HashSet<Integer>();
		ArrayList<String> res=new ArrayList<String>();
		HashMap<Character, Integer> map = new HashMap<Character, Integer>();
		map.put('A',0);map.put('C',1);map.put('G',2);map.put('T',3);
		int basenum = (int) Math.pow(map.size(), 9);
		int hash=0;
		for(int i=0;i<s.length(); i++){
			if(i>9){
				hash-=basenum* map.get(s.charAt(i-10));
			}
			hash=hash*map.size()+ map.get(s.charAt(i));
			if(i>8){
				if(!set.add(hash) && rset.add(hash)){
					res.add(s.substring(i-9,i+1));
				}
			}
		}
		return res;
	}


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(findRepeatedDnaSequences(""));
	}

}
